function batches_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('batches');
	if(!$arrPerm[1]){
		return false;
	}

	$data['item'] = makeSafe($_REQUEST['item']);
		if($data['item'] == empty_lookup_value){ $data['item'] = ''; }
	$data['supplier'] = makeSafe($_REQUEST['supplier']);
		if($data['supplier'] == empty_lookup_value){ $data['supplier'] = ''; }
	$data['batch_no'] = makeSafe($_REQUEST['batch_no']);
		if($data['batch_no'] == empty_lookup_value){ $data['batch_no'] = ''; }
	$data['manufacturing_date'] = intval($_REQUEST['manufacturing_dateYear']) . '-' . intval($_REQUEST['manufacturing_dateMonth']) . '-' . intval($_REQUEST['manufacturing_dateDay']);
	$data['manufacturing_date'] = parseMySQLDate($data['manufacturing_date'], '1');
	$data['expiry_date'] = intval($_REQUEST['expiry_dateYear']) . '-' . intval($_REQUEST['expiry_dateMonth']) . '-' . intval($_REQUEST['expiry_dateDay']);
	$data['expiry_date'] = parseMySQLDate($data['expiry_date'], '1');
	if($data['balance'] == '') $data['balance'] = "0.00";

	// hook: batches_before_insert
	if(function_exists('batches_before_insert')){
		$args=array();
		if(!batches_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `batches` set       `item`=' . (($data['item'] !== '' && $data['item'] !== NULL) ? "'{$data['item']}'" : 'NULL') . ', `supplier`=' . (($data['supplier'] !== '' && $data['supplier'] !== NULL) ? "'{$data['supplier']}'" : 'NULL') . ', `batch_no`=' . (($data['batch_no'] !== '' && $data['batch_no'] !== NULL) ? "'{$data['batch_no']}'" : 'NULL') . ', `manufacturing_date`=' . (($data['manufacturing_date'] !== '' && $data['manufacturing_date'] !== NULL) ? "'{$data['manufacturing_date']}'" : 'NULL') . ', `expiry_date`=' . (($data['expiry_date'] !== '' && $data['expiry_date'] !== NULL) ? "'{$data['expiry_date']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"batches_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: batches_after_insert
	if(function_exists('batches_after_insert')){
		$res = sql("select * from `batches` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!batches_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='batches', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

function batches_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('batches');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='batches' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='batches' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: batches_before_delete
	if(function_exists('batches_before_delete')){
		$args=array();
		if(!batches_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: transactions
	$res = sql("select `id` from `batches` where `id`='$selected_id'", $eo);
	$id = db_fetch_row($res);
	$rires = sql("select count(1) from `transactions` where `batch`='".addslashes($id[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "transactions", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "transactions", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='batches_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='batches_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `batches` where `id`='$selected_id'", $eo);

	// hook: batches_after_delete
	if(function_exists('batches_after_delete')){
		$args=array();
		batches_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='batches' and pkValue='$selected_id'", $eo);
}

function batches_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('batches');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='batches' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='batches' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['item'] = makeSafe($_REQUEST['item']);
		if($data['item'] == empty_lookup_value){ $data['item'] = ''; }
	$data['supplier'] = makeSafe($_REQUEST['supplier']);
		if($data['supplier'] == empty_lookup_value){ $data['supplier'] = ''; }
	$data['batch_no'] = makeSafe($_REQUEST['batch_no']);
		if($data['batch_no'] == empty_lookup_value){ $data['batch_no'] = ''; }
	$data['manufacturing_date'] = intval($_REQUEST['manufacturing_dateYear']) . '-' . intval($_REQUEST['manufacturing_dateMonth']) . '-' . intval($_REQUEST['manufacturing_dateDay']);
	$data['manufacturing_date'] = parseMySQLDate($data['manufacturing_date'], '1');
	$data['expiry_date'] = intval($_REQUEST['expiry_dateYear']) . '-' . intval($_REQUEST['expiry_dateMonth']) . '-' . intval($_REQUEST['expiry_dateDay']);
	$data['expiry_date'] = parseMySQLDate($data['expiry_date'], '1');
	$data['selectedID']=makeSafe($selected_id);

	// hook: batches_before_update
	if(function_exists('batches_before_update')){
		$args=array();
		if(!batches_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `batches` set       `item`=' . (($data['item'] !== '' && $data['item'] !== NULL) ? "'{$data['item']}'" : 'NULL') . ', `supplier`=' . (($data['supplier'] !== '' && $data['supplier'] !== NULL) ? "'{$data['supplier']}'" : 'NULL') . ', `batch_no`=' . (($data['batch_no'] !== '' && $data['batch_no'] !== NULL) ? "'{$data['batch_no']}'" : 'NULL') . ', `manufacturing_date`=' . (($data['manufacturing_date'] !== '' && $data['manufacturing_date'] !== NULL) ? "'{$data['manufacturing_date']}'" : 'NULL') . ', `expiry_date`=' . (($data['expiry_date'] !== '' && $data['expiry_date'] !== NULL) ? "'{$data['expiry_date']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="batches_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: batches_after_update
	if(function_exists('batches_after_update')){
		$res = sql("SELECT * FROM `batches` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!batches_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='batches' and pkValue='".makeSafe($selected_id)."'", $eo);

}

